import React, { useState } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";
import "../styles/Dashboard.css";
import irrigationBg from "../assets/irrigation-bg.jpg";

const GroundwaterDashboard = () => {
  const [latitude, setLatitude] = useState("");
  const [longitude, setLongitude] = useState("");
  const [error, setError] = useState("");
  const [data, setData] = useState([]);
  const [currentMonthData, setCurrentMonthData] = useState(null);
  const [selectedMonth, setSelectedMonth] = useState(null);
  const [showDashboard, setShowDashboard] = useState(false);

  const monthNames = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const getLevelClass = (level) => {
    const l = level.toLowerCase();
    if (l === "low") return "level-low";
    if (l === "moderate") return "level-moderate";
    if (l === "high") return "level-high";
    return "";
  };

  const handlePredict = async () => {
    const lat = parseFloat(latitude);
    const lon = parseFloat(longitude);
    setError("");
    setShowDashboard(false);
    setCurrentMonthData(null);
    setSelectedMonth(null);
    setData([]);

    if (isNaN(lat) || lat < -90 || lat > 90) {
      setError("Enter a valid latitude (-90 to 90)");
      return;
    }
    if (isNaN(lon) || lon < -180 || lon > 180) {
      setError("Enter a valid longitude (-180 to 180)");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ latitude: lat, longitude: lon })
      });
      if (!res.ok) {
        const err = await res.json();
        setError(err.error || "Something went wrong.");
        return;
      }
      const result = await res.json();
      setData(result);

      const currentMonth = new Date().getMonth() + 1;
      const current = result.find((d) => d.month === currentMonth);
      setCurrentMonthData(current);
      setSelectedMonth(currentMonth);
      setShowDashboard(true);
    } catch (err) {
      setError("Error connecting to the backend.");
    }
  };

  const chartData = data.map((item) => ({
    month: monthNames[item.month - 1],
    level: item.level === "Low" ? 1 : item.level === "Moderate" ? 2 : 3
  }));

  return (
    <div className="container" style={{ backgroundImage: `url(${irrigationBg})`, backgroundSize: "cover", minHeight: "100vh", padding: "20px" }}>
      <h1>Groundwater Level Forecast</h1>

      <div className="form-group">
        <label>Latitude (-90 to 90):</label>
        <input type="number" value={latitude} onChange={(e) => setLatitude(e.target.value)} placeholder="Enter latitude" />
      </div>

      <div className="form-group">
        <label>Longitude (-180 to 180):</label>
        <input type="number" value={longitude} onChange={(e) => setLongitude(e.target.value)} placeholder="Enter longitude" />
      </div>

      <button onClick={handlePredict}>Get Forecast</button>
      {error && <div className="error">{error}</div>}

      {showDashboard && (
        <div className="dashboard">
          <div className="card">
            <h2>{monthNames[currentMonthData.month - 1]} Forecast</h2>
            <p>
              <strong>Groundwater Level:</strong>{" "}
              <span className={getLevelClass(currentMonthData.level)}>
                {currentMonthData.level}
              </span>
            </p>
            <p><strong>Temperature:</strong> {(25 + Math.random() * 5).toFixed(1)} Â°C</p>
            <p><strong>Humidity:</strong> {(50 + Math.random() * 20).toFixed(0)} %</p>
          </div>

          <div className="form-group">
            <label>Explore Another Month:</label>
            <select value={selectedMonth} onChange={(e) => setSelectedMonth(parseInt(e.target.value))}>
              {data.map((item) => (
                <option key={item.month} value={item.month}>
                  {monthNames[item.month - 1]}
                </option>
              ))}
            </select>
          </div>

          {selectedMonth && (
            <div className="card">
              <h2>{monthNames[selectedMonth - 1]}</h2>
              <p>
                <strong>Groundwater Level:</strong>{" "}
                <span className={getLevelClass(data.find((d) => d.month === selectedMonth).level)}>
                  {data.find((d) => d.month === selectedMonth).level}
                </span>
              </p>
            </div>
          )}

          <h3 style={{ marginTop: "40px" }}>Monthly Forecast Flow</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={chartData} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="month" />
              <YAxis ticks={[1, 2, 3]} domain={[0, 4]} tickFormatter={(val) => ["", "Low", "Moderate", "High"][val]} />
              <Tooltip formatter={(value) => ["Low", "Moderate", "High"][value]} />
              <Line type="monotone" dataKey="level" stroke="#007bff" strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
};

export default GroundwaterDashboard;
